var Portfolio=function(){return{init:function(){var b=$(".portfolio-filter");var a=$(".portfolio");var c;b.find("a").on("click",function(){c=$(this).data("category");if(!$(this).hasClass("active")){b.find("a").removeClass("active");$(this).addClass("active");if(c==="all"){a.find(".portfolio-item").hide(0,function(){$(this).show(0)})}else{a.find(".portfolio-item").hide(0,function(){a.find('.portfolio-item[data-category="'+c+'"]').show(0)})}}})}}}();